class Laser{constructor(s,t){this.pos=createVector(s.x,s.y),this.velocity=p5.Vector.fromAngle(t),this.velocity.mult(10)}update(){this.pos.add(this.velocity)}render(){push(),stroke(255),strokeWeight(4),point(this.pos.x,this.pos.y),pop()}hits(s){return dist(this.pos.x,this.pos.y,s.pos.x,s.pos.y)<s.radius}offscreen(){return this.pos.x>width||this.pos.x<0||(this.pos.y>height||this.pos.y<0)}}