class Ship{constructor(){this.pos=createVector(width/2,height/2),this.radius=10,this.heading=0,this.rotation=0,this.velocity=createVector(0,0),this.isBoosting=!1}boosting(s){this.isBoosting=s}update(){this.isBoosting&&this.boost(),this.pos.add(this.velocity),this.velocity.mult(.99)}boost(){var s=p5.Vector.fromAngle(this.heading);s.mult(.1),this.velocity.add(s)}hits(s){return dist(this.pos.x,this.pos.y,s.pos.x,s.pos.y)<this.radius+s.radius}render(){push(),translate(this.pos.x,this.pos.y),rotate(this.heading+PI/2),fill(0),stroke(255),triangle(-this.radius,this.radius,this.radius,this.radius,0,-this.radius),pop()}edges(){this.pos.x>width+this.radius?this.pos.x=-this.radius:this.pos.x<-this.radius&&(this.pos.x=width+this.radius),this.pos.y>height+this.radius?this.pos.y=-this.radius:this.pos.y<-this.radius&&(this.pos.y=height+this.radius)}setRotation(s){this.rotation=s}turn(){this.heading+=this.rotation/3}}